import React, { useState, useEffect } from "react";
import MovieDataService from "../services/movies";
import { useParams } from "react-router-dom";
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';
import Card from 'react-bootstrap/Card';
import Image from "react-bootstrap/Image";

const Movie = props => {
    let params = useParams();

    const [movie, setMovie] = useState({
        id:null,
        title:"",
        rated:"",
        poster:"",
        reviews:[]
    });

    useEffect(() => {
        const getMovie = id => {
            MovieDataService.findById(id)
            .then(response => {
            console.log(response.data);
            let movieRes = response.data;
            if (!movieRes.hasOwnProperty("poster") ) {

                console.log("mamamamamamama");
            }else {
                console.log("kakakakakakaka");
            }

            setMovie(response.data);
            }).catch(e => {
                console.log(e);
            })

        }
        getMovie(params.id);
    }, [params.id]);

    return (
        <div>
            <Container>
                <Row>
                    <Col>
                        <div className="poster">
                            <Image
                                className="bigPicture"
                                src={movie.poster+"/100px250"}
                                fluid />
                        </div>
                    </Col>
                    <Col>
                        <Card>
                            <Card.Header as="h5">
                                {movie.title}
                            </Card.Header>
                            <Card.Body>
                                <Card.Text>
                                    {movie.plot}
                                </Card.Text>
                            </Card.Body>
                        </Card>
                        <h2>
                            Reviews
                        </h2>
                        <br></br>
                        {movie.reviews.map((review, index) => {
                            return (
                                <div className="d-flex">
                                    <div className="flex-shrink-0 reviewText">
                                        <h5>
                                            {review.name + " reviewd on "}
                                        </h5>
                                        <p className="review">{review.review}</p>
                                    </div>
                                </div>
                            )
                        })}
                    </Col>
                </Row>
            </Container>
        </div>
    );
}

export default Movie;
