import update from "immutability-helper";
import { useCallback, useState, useEffect } from "react";
import { Card } from "./FavoriteCard.js";
import style from "./favorites.css"
import FavoritesDataService from "../services/favorites";
const mStyle = {
  width: 500,
  margin: 1
}

const FavoriteContainer = ({
  user,
  favorites,
  addFavorite,
  deleteFavorite
}) => {
  const [movies, setMovies] = useState([]);
  const [myFavorites,setMyFavorites] = useState([favorites]);
  const addMoviesIndex = (favorites,movies) => {
    let moviesWithIndex = [];
    for(let i = 0;i<favorites.length;i++) {
     for(let j = 0;j < movies.legnth;j++) {
       console.error(favorites[i] + "   "+ movies[j]._id);
       if(favorites[i] == movies[j]._id) {
         movies[j].index = i;
         moviesWithIndex.push(movies);
       }
     }
    }
    return moviesWithIndex;

   };
  useEffect(() => {
    const getMovie = favorites => {
      FavoritesDataService.getFavoriteListByMovieIds(favorites)
        .then(response => {
         let  movies =  addMoviesIndex(favorites, response.data.movies);
          console.error(movies);
          setMovies(movies);
        })
        .catch(e => {
          console.log(e);
        });
    }
    getMovie(favorites);
    setMyFavorites(favorites);
  }, [favorites]);


  const moveCard = useCallback((dragIndex, hoverIndex) => {
    setMovies((prevMovies) =>
      update(prevMovies, {
        $splice: [
          [dragIndex, 1],
          [hoverIndex, 0, prevMovies[dragIndex]]
        ]
      })
    );
    setMyFavorites((prevMyFavorites) =>
      update(prevMyFavorites, {
        $splice: [
          [dragIndex, 1],
          [hoverIndex, 0, prevMyFavorites[dragIndex]]
        ]
      })
    );
  }, []);
  const renderCard = useCallback((movie, index) => {
    return (
      <div>
        <Card
          key={movie._id}
          index={index}
          id={movie._id}
          movie={movie}
          moveCard={moveCard}
        />
      </div>
    );
  }, []);

  return (
    <div className="favoritesContainer container">
      <div className="favoritesPanel">
        Drag your favorites to rank them
      </div>
      <div>
        {movies.map((movie,i) => renderCard(movie, movie.index))}
      </div>
    </div>
  );

};
export default FavoriteContainer;
