import { GoogleOAuthProvider } from '@react-oauth/google';

import { Routes, Route, Link } from 'react-router-dom';
import "bootstrap/dist/css/bootstrap.min.css";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { useState, useEffect ,useCallback} from 'react';
import Login from './components/Login';
import Logout from './components/Logout';


import MoviesList from "./components/MoviesList"
import Movie from "./components/Movie"
import AddReview from "./components/AddReview"
import FavoritesDataService from "./services/favorites";


import './App.css';
import { NavbarBrand } from 'react-bootstrap';

const clientId = process.env.REACT_APP_GOOGLE_CLIENT_ID;

function App() {


  const [user, setUser] = useState(null);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    const getFavorite = id => {
        MovieDataService.findById(id)
            .then(response => {
                console.log(response.data);
                setMovie(response.data);
            }).catch(e => {
                console.log(e);
            })

    }
    getMovie(params.id);
}, [params.id]);

  const addFavorite = useCallback((user,movieId) => {
    setFavorites([...favorites, movieId]);
    console.log(" add favorite ");
    console.log(favorites);
    console.log(movieId);
    console.log(user);
    var data = {
      _id: user.googleId,
      favorites: [...favorites, movieId],
    }
    FavoritesDataService.updateFavorites(data);

  }, ...favorites);
  // const addFavorite =  useCallback((movieId) => {
  //   setFavorites([...favorites, movieId])
  //   console.log(favorites);
  // });

  const deleteFavorite = (movieId) => {
    setFavorites(favorites.filter(f => f !== movieId));
  }

  useEffect(() => {
    let loginData = JSON.parse(localStorage.getItem("login"));
    if (loginData) {
      let loginExp = loginData.exp;
      let now = Date.now() / 1000;
      if (now < loginExp) {
        // Not expired
        setUser(loginData);
      } else {
        // Expired
        localStorage.setItem("login", null);
      }
    }
  }, []);

  return (
    <GoogleOAuthProvider clientId={clientId}>

      <div className="App">
        <Navbar bg="primary" expand="lg" sticky="top" variant="dark">
          <Container className="container-fluid">
            <Navbar.Brand className="brand" href="/">
              <img src="/images/movies-logo.png" alt="movie logo" className="moviesLogo" />
              MOVIE TIME
            </Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="ml-auto">
                <Nav.Link as={Link} to={"/movies"}>
                  Movies
                </Nav.Link>
              </Nav>
            </Navbar.Collapse>
            {user ? (
              <Logout setUser={setUser} />
            ) : (
              <Login setUser={setUser} />

            )}
          </Container>
        </Navbar>
        <Routes>
          <Route exact path={"/"} element={
            <MoviesList
              user={ user }
              addFavorite={ addFavorite }
              deleteFavorite={ deleteFavorite }
              favorites={ favorites }
             />}
          />
          <Route exact path={"/movies"} element={
            <MoviesList
              user={ user }
              addFavorite={ addFavorite }
              deleteFavorite={ deleteFavorite }
              favorites={ favorites } 
            />}
          />
          <Route exact path={"/movies/:id/"} element={
            <Movie user={ user } />}
          />
          <Route exact path={"/movies/:id/review"} element={
            <AddReview user={ user } />}
          />
        </Routes>
      </div>
    </GoogleOAuthProvider>
  );
}

export default App;
